package game;
import java.util.*;
public class App {

	public static void main(String[] args) {
		// Get the number of players
		System.out.println("Enter the number of players to play: ");
		Scanner inputValue=new Scanner(System.in);
		int numberOfPlayers=inputValue.nextInt();
		
		// Create a GameBoard object
		GameBoard gameboard=new GameBoard(numberOfPlayers);
		
		// Start playing
		// Show a completion message with the name of the winner
		System.out.println("Yah wow! Game Over Winner is:  "+gameboard.play());
	}

}

